    /**
     * Parse the hardcoded JSON data into a list of individual JSON objects
     */
    fun getRealSensorDataList(): List<String> {
        return try {
            // Parse the JSON array and return individual JSON objects as strings
            val jsonArray = org.json.JSONArray(REAL_SENSOR_DATA_JSON)
            val dataList = mutableListOf<String>()
            
            for (i in 0 until jsonArray.length()) {
                val jsonObject = jsonArray.getJSONObject(i)
                dataList.add(jsonObject.toString())
            }
            
            dataList
        } catch (e: Exception) {
            // Fallback to empty list if parsing fails
            emptyList()
        }
    }
    
    /**
     * Get a random sensor data entry from the real data
     */
    fun getRandomSensorData(): String {
        val dataList = getRealSensorDataList()
        return if (dataList.isNotEmpty()) {
            dataList.random()
        } else {
            // Fallback to a simple JSON if parsing fails
            """{"SS": 74.961,"Cu": 24.555,"Zn": 0.68,"Ag": 3592.38,"Pt": 3504.4,"Temp": 30.62,"pH": 0.004,"TDS": 0.536,"UV": 0.017,"Soil": 0.536,"timestamp": "2025-09-10T17:18:14.299774"}"""
        }
    }
    
    /**
     * Get sensor data in sequence (cycling through the real data)
     */
    private var currentIndex = 0
    fun getNextSensorData(): String {
        val dataList = getRealSensorDataList()
        return if (dataList.isNotEmpty()) {
            val data = dataList[currentIndex % dataList.size]
            currentIndex++
            data
        } else {
            getRandomSensorData()
        }
    }
